var hypothek = data.Hypothekarbetrag || 0;
var kaufpreis = data.Summe_Kaufpreis_Immo || 0;
var amortisation = data.jahrlichFalligerBetrag1 || 0;

// Utility: Sum all incomes
function sumIncome(grid) {
  return (grid || []).reduce(function(sum, row) {
    return sum +
      (row.bruttoeinkommen_Angestellt || 0) +
      (row.bruttoeinkommen_Selbstaendig || 0) +
      (row.bruttoeinkommen_Mieteinnahmen || 0);
  }, 0);
}

// Utility: Sum all expenses
function sumExpense(grid) {
  return (grid || []).reduce(function(sum, row) {
    return sum +
      (row.monatlicheBelastung_Kredite || 0) +
      (row.monatlicheBelastung_Leasing || 0) +
      (row.monatlicheBelastung_Alimente || 0);
  }, 0);
}

// Loop through all persons' income/expense grids
var totalIncome =
  sumIncome(data.income1) +
  sumIncome(data.income2) +
  sumIncome(data.income3) +
  sumIncome(data.income4) +
  sumIncome(data.income5);

var totalExpenses =
  sumExpense(data.expense1) +
  sumExpense(data.expense2) +
  sumExpense(data.expense3) +
  sumExpense(data.expense4) +
  sumExpense(data.expense5);

var totalNettoeinkommen = totalIncome - totalExpenses;

// Belastung calculation (remove park/renovation!)
var totalBelastung =
  hypothek * 0.05 + kaufpreis * 0.01 + amortisation;

// Output
if (hypothek === 0 || totalNettoeinkommen === 0) {
  value = '—';
} else {
  value = (totalBelastung / totalNettoeinkommen * 100).toFixed(1) + ' %';
